// --- CONFIGURACIÓN DEL MAPA ---
const WIDTH = 500; 
const HEIGHT = 200; 
const TOTAL_PIXELS = WIDTH * HEIGHT;
const MAP_CONTAINER_ID = 'pixel-map-container';

// --- ESTADO GLOBAL Y CONFIGURACIÓN CRÍTICA ---
const INITIAL_PRICE_USDC = 1.00; 
let selectedPixels = new Set(); 
let mapData = []; 
let currentAccount = null; 
let currentPixel = { x: null, y: null }; 
let pixelContract; 

// --- VARIABLES DE ZOOM/ARRRASTRE ---
// El WRAPPER debe ser accedido después de que el DOM cargue
let WRAPPER;
let MAP_ELEMENT;
let scale = 1.0;
let position = { x: 0, y: 0 };
let isDragging = false;
let startDrag = { x: 0, y: 0 };


// --- PLACEHOLDERS DE BLOCKCHAIN (NECESARIOS PARA MUHAMMAD) ---
const CONTRACT_ADDRESS = "DIRECCION_DE_CONTRATO_AQUI"; 
const PIXEL_CONTRACT_ABI = []; 
const USDC_ADDRESS = "0x2791Bca1f2de4661ED88A30C99A7a922Aa845041"; 

// --- COLORES ---
const LAND_COLOR = '#008000'; 
const SEA_COLOR = '#0000FF'; 

// --- FUNCIÓN DE COORDENADAS AMIGABLES ---
const COORD_MAP_TO_UI = (x, y) => {
    let colStr = '';
    let i = x;
    while (i >= 0) {
        colStr = String.fromCharCode(i % 26 + 'A'.charCodeAt(0)) + colStr;
        i = Math.floor(i / 26) - 1;
    }
    const rowStr = y + 1; 
    return `${colStr}-${rowStr}`;
};

// =========================================================
// PARTE I: MATRIZ DE DATOS DEL MAPA PERSONALIZADO (CÓDIGO GENERADO)
// =========================================================

// Esta matriz codificada simplifica el mapeo: '1' = Tierra (LAND), '0' = Mar (SEA).
// Esta matriz es la representación final y corregida de tu diseño de Pixel Art.
const MAP_DATA_CODED = `
`

// La función 'parse' se asume que existe para convertir la matriz codificada.
function getPixelFromCodedMap(x, y) {
    const char = MAP_DATA_CODED.charAt(y * WIDTH + x);
    return (char === '1') ? LAND_COLOR : SEA_COLOR;
}

// Inicializa los datos (Hard Reset: Todos Libres)
function initializeMapData() {
    for (let y = 0; y < HEIGHT; y++) {
        for (let x = 0; x < WIDTH; x++) {
            const index = y * WIDTH + x;
            
            // Lógica de color: usa la matriz codificada
            const color = getInitialMapColor(x, y); 
            
            // LÓGICA DE PROPIEDAD DE LANZAMIENTO: TODOS LIBRES
            const isOwned = false; 
            const owner = 'Nadie'; 
            const price = INITIAL_PRICE_USDC; 

            mapData[index] = {
                x,
                y,
                color: color,
                owner: owner,
                price: price,
                isOwned: isOwned
            };
        }
    }
}
function getInitialMapColor(x, y) {
    const index = y * WIDTH + x;
    const codedValue = MAP_DATA_CODED.charAt(index);
    return codedValue === '1' ? LAND_COLOR : SEA_COLOR;
}


// =========================================================
// PARTE II: RENDERING Y EVENTOS (Frontend)
// =========================================================

function renderMap() {
    // === CORRECCIÓN DE BINDING ===
    MAP_ELEMENT = document.getElementById(MAP_CONTAINER_ID);
    WRAPPER = document.getElementById('map-interaction-wrapper');
    // =============================
    
    // Limpiar mapa anterior
    MAP_ELEMENT.innerHTML = ''; 

    mapData.forEach(pixel => {
        const pixelElement = document.createElement('div');
        pixelElement.className = 'pixel';
        pixelElement.style.backgroundColor = pixel.color;
        
        if (pixel.isOwned) {
            pixelElement.setAttribute('data-owner-status', 'owned');
        }

        pixelElement.dataset.x = pixel.x;
        pixelElement.dataset.y = pixel.y;

        pixelElement.addEventListener('click', handlePixelClick);
        pixelElement.addEventListener('contextmenu', handlePixelRightClick); 

        MAP_ELEMENT.appendChild(pixelElement);
    });
    
    // Aplicar transformaciones iniciales
    updateMapTransform(); 
}

// Lógica de clic izquierdo (o modal)
function handlePixelClick(event) {
    // Si estamos en medio de un arrastre, ignorar el clic
    if (Math.abs(startDrag.x - event.clientX) > 5 || Math.abs(startDrag.y - event.clientY) > 5) {
        return; 
    }
    
    const pixelElement = event.target;
    const x = parseInt(pixelElement.dataset.x);
    const y = parseInt(pixelElement.dataset.y);
    const index = y * WIDTH + x;
    const pixel = mapData[index];

    showModal(pixel, pixelElement);
}

// Lógica de clic derecho (o lote)
function handlePixelRightClick(event) {
    const targetElement = event.target || event; 
    
    if (event.preventDefault) {
        event.preventDefault(); 
    }
    
    const pixelElement = targetElement;
    const x = parseInt(pixelElement.dataset.x);
    const y = parseInt(pixelElement.dataset.y);
    const id = `${x},${y}`;
    const pixel = mapData[y * WIDTH + x];

    if (!pixel.isOwned) {
        if (selectedPixels.has(id)) {
            selectedPixels.delete(id);
            pixelElement.classList.remove('selected');
        } else {
            selectedPixels.add(id);
            pixelElement.classList.add('selected');
        }
        updateCartSummary();
    } else {
        if (event.preventDefault) { 
            alert("Este píxel está ocupado o en venta. Click izquierdo para ver detalles.");
        }
    }
}

function updateCartSummary() {
    const count = selectedPixels.size;
    const totalCost = count * INITIAL_PRICE_USDC; 
    
    document.getElementById('cart-summary').textContent = 
        `Cesta: ${count} píxeles seleccionados | Total: ${totalCost.toFixed(2)} USDC`;
        
    document.getElementById('pay-batch-btn').disabled = count === 0;
}

function clearCart() {
    selectedPixels.forEach(id => {
        const [x, y] = id.split(',').map(Number);
        const element = document.querySelector(`.pixel[data-x="${x}"][data-y="${y}"]`);
        if (element) {
            element.classList.remove('selected');
        }
    });
    selectedPixels.clear();
    updateCartSummary();
}


function showModal(pixel, element) {
    document.getElementById('modal-coords').textContent = `(X:${pixel.x}, Y:${pixel.y})`;
    document.getElementById('modal-price').textContent = `${pixel.price.toFixed(2)} USDC`;
    document.getElementById('modal-owner').textContent = pixel.owner;
    document.getElementById('modal-color-display').textContent = pixel.color;
    
    const isUserOwner = (pixel.owner === '0xAbcD...1234'); 

    document.getElementById('owner-controls').style.display = isUserOwner ? 'block' : 'none'; 
    const buyBtn = document.getElementById('buy-pixel-btn');

    if (!pixel.isOwned) {
        buyBtn.textContent = 'AÑADIR A CESTA (Lote)';
        buyBtn.onclick = () => {
            handlePixelRightClick({ preventDefault: () => {}, target: element });
            document.getElementById('modal-backdrop').style.display = 'none';
        };
    } else {
        buyBtn.textContent = `COMPRAR (Reventa) por ${pixel.price.toFixed(2)} USDC`;
        buyBtn.onclick = () => {
            alert(`Simulación de Compra Individual/Reventa. Precio: ${pixel.price.toFixed(2)} USDC.`);
            document.getElementById('modal-backdrop').style.display = 'none';
        };
    }

    document.getElementById('modal-backdrop').style.display = 'flex';
}

function hideModal() {
    document.getElementById('modal-backdrop').style.display = 'none';
}


// =========================================================
// PARTE IV: LÓGICA DE ZOOM Y ARRASTRE
// =========================================================

function updateMapTransform() {
    // Aplica la escala y la posición al elemento del mapa
    MAP_ELEMENT.style.transform = `translate(${position.x}px, ${position.y}px) scale(${scale})`;
    WRAPPER.style.cursor = isDragging ? 'grabbing' : 'grab';
}

function handleZoom(event) {
    // Si no se usa el botón CTRL, el zoom está desactivado para evitar conflictos de scroll
    if (!event.ctrlKey) return; 
    event.preventDefault();

    const zoomFactor = event.deltaY > 0 ? 0.9 : 1.1; // 10% más o menos
    const newScale = Math.max(1.0, Math.min(scale * zoomFactor, 10.0)); // Límites de zoom 1x a 10x
    
    if (newScale !== scale) {
        // Ajusta la posición para centrar el zoom en el cursor
        const rect = WRAPPER.getBoundingClientRect();
        const mouseX = event.clientX - rect.left;
        const mouseY = event.clientY - rect.top;

        const ratio = newScale / scale;

        position.x = mouseX - (mouseX - position.x) * ratio;
        position.y = mouseY - (mouseY - position.y) * ratio;

        scale = newScale;
        updateMapTransform();
    }
}

function handleMouseDown(event) {
    if (event.button !== 0) return; // Solo clic izquierdo
    isDragging = true;
    startDrag.x = event.clientX;
    startDrag.y = event.clientY;
    MAP_ELEMENT.style.transition = 'none'; // Deshabilita la transición al arrastrar
    updateMapTransform();
}

function handleMouseMove(event) {
    if (!isDragging) return;
    const dx = event.clientX - startDrag.x;
    const dy = event.clientY - startDrag.y;
    
    position.x += dx;
    position.y += dy;
    
    startDrag.x = event.clientX;
    startDrag.y = event.clientY;
    
    updateMapTransform();
}

function handleMouseUp() {
    isDragging = false;
    MAP_ELEMENT.style.transition = 'transform 0.1s ease-out'; // Reactiva la transición al soltar
    updateMapTransform();
}


// =========================================================
// PARTE V: INICIALIZACIÓN FINAL
// =========================================================

document.addEventListener('DOMContentLoaded', () => {
    // Asigna los elementos globales al inicio del DOMContentLoaded
    WRAPPER = document.getElementById('map-interaction-wrapper');
    MAP_ELEMENT = document.getElementById(MAP_CONTAINER_ID);

    initializeMapData();
    renderMap();
    
    // === LISTENERS DE ZOOM Y ARRASTRE ===
    WRAPPER.addEventListener('wheel', handleZoom, { passive: false });
    WRAPPER.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    
    // --- CONEXIÓN DE BOTONES ---
    document.getElementById('connect-wallet').addEventListener('click', connectWallet);
    document.getElementById('pay-batch-btn').addEventListener('click', executeBatchPurchase);
    document.getElementById('clear-cart-btn').addEventListener('click', clearCart);
    document.getElementById('close-modal-btn').addEventListener('click', hideModal);
    
    // Conectar botones de Dueño (SIMULADO)
    document.getElementById('set-color-btn').addEventListener('click', () => {
        const newColor = document.getElementById('new-color').value;
        alert(`Simulación: Cambiando color a ${newColor}.`);
        hideModal();
    });
    
    document.getElementById('sell-pixel-btn').addEventListener('click', () => {
        const newPrice = document.getElementById('new-price').value;
        alert(`Simulación: Poniendo píxel en venta por ${newPrice} USDC.`);
        hideModal();
    });
});

